  async updateProgressWithSRS(wordId: number, correct: boolean, responseTime: number = 0): Promise<void> {
    const progressKey = `progress_${wordId}`;
    const existingProgress = await storage.getItem(progressKey);
    
    let progress: UserProgress;
    
    if (existingProgress) {
      progress = JSON.parse(existingProgress);
    } else {
      progress = {
        id: Date.now(),
        wordId,
        attempts: 0,
        correctAnswers: 0,
        lastSeen: new Date().toISOString(),
        easeFactor: 2.5,
        interval: 1,
        nextReview: new Date().toISOString(),
        repetitions: 0,
        responseTime: 0,
        streakCount: 0,
        lastResponseCorrect: false
      };
    }
    
    progress.attempts += 1;
    progress.lastSeen = new Date().toISOString();
    progress.responseTime = responseTime;
    progress.lastResponseCorrect = correct;
    
    if (correct) {
      progress.correctAnswers += 1;
      progress.repetitions += 1;
      progress.streakCount += 1;
      
      if (progress.repetitions === 1) {
        progress.interval = 1;
      } else if (progress.repetitions === 2) {
        progress.interval = 6;
      } else {
        progress.interval = Math.round(progress.interval * progress.easeFactor);
      }
      
      progress.easeFactor = Math.max(1.3, progress.easeFactor + (0.1 - (5 - 3) * (0.08 + (5 - 3) * 0.02)));
    } else {
      progress.repetitions = 0;
      progress.interval = 1;
      progress.streakCount = 0;
      progress.easeFactor = Math.max(1.3, progress.easeFactor - 0.2);
    }
    
    const nextReview = new Date();
    nextReview.setDate(nextReview.getDate() + progress.interval);
    progress.nextReview = nextReview.toISOString();
    
    await storage.setItem(progressKey, JSON.stringify(progress));
  }

  async getWordsDueForReview(difficulty?: 'beginner' | 'intermediate' | 'advanced' | 'expert'): Promise<Word[]> {
    await this.init();
    const now = new Date();
    const dueWords: Word[] = [];
    
    let wordsToCheck = this.words;
    if (difficulty) {
      wordsToCheck = this.words.filter(word => word.difficulty === difficulty);
    }
    
    for (const word of wordsToCheck) {
      const progress = await this.getUserProgress(word.id);
      if (!progress) {
        dueWords.push(word);
      } else {
        const nextReview = new Date(progress.nextReview);
        if (nextReview <= now) {
          dueWords.push(word);
        }
      }
    }
    
    return dueWords;
  }

  async getAdaptiveDifficultyWord(currentDifficulty: 'beginner' | 'intermediate' | 'advanced' | 'expert'): Promise<Word | null> {
    await this.init();
    const difficultyOrder = ['beginner', 'intermediate', 'advanced', 'expert'];
    const currentIndex = difficultyOrder.indexOf(currentDifficulty);
    
    const progress = await this.getDifficultyProgress(currentDifficulty);
    const accuracyThreshold = 0.8;
    const completionThreshold = 0.7;
    
    let targetDifficulty = currentDifficulty;
    
    if (progress.accuracy > accuracyThreshold && 
        (progress.learnedWords / progress.totalWords) > completionThreshold &&
        currentIndex < difficultyOrder.length - 1) {
      targetDifficulty = difficultyOrder[currentIndex + 1] as 'beginner' | 'intermediate' | 'advanced' | 'expert';
    } else if (progress.accuracy < 0.5 && currentIndex > 0) {
      targetDifficulty = difficultyOrder[currentIndex - 1] as 'beginner' | 'intermediate' | 'advanced' | 'expert';
    }
    
    const dueWords = await this.getWordsDueForReview(targetDifficulty);
    if (dueWords.length === 0) {
      return await this.getRandomWord(targetDifficulty);
    }
    
    const randomIndex = Math.floor(Math.random() * dueWords.length);
    return dueWords[randomIndex];
  }

  async getLearningAnalytics(): Promise<LearningAnalytics> {
    await this.init();
    const allProgress = await this.getAllUserProgress();
    
    let totalResponseTime = 0;
    let totalAttempts = 0;
    let totalCorrect = 0;
    let currentStreak = 0;
    let maxStreak = 0;
    
    const categoryStats: Record<string, {correct: number, total: number}> = {};
    
    for (const progress of allProgress) {
      totalAttempts += progress.attempts;
      totalCorrect += progress.correctAnswers;
      totalResponseTime += progress.responseTime * progress.attempts;
      
      if (progress.streakCount > maxStreak) {
        maxStreak = progress.streakCount;
      }
      
      const word = this.words.find(w => w.id === progress.wordId);
      if (word) {
        if (!categoryStats[word.difficulty]) {
          categoryStats[word.difficulty] = {correct: 0, total: 0};
        }
        categoryStats[word.difficulty].correct += progress.correctAnswers;
        categoryStats[word.difficulty].total += progress.attempts;
      }
    }
    
    let strongestCategory = '';
    let weakestCategory = '';
    let bestAccuracy = 0;
    let worstAccuracy = 1;
    
    for (const [category, stats] of Object.entries(categoryStats)) {
      const accuracy = stats.total > 0 ? stats.correct / stats.total : 0;
      if (accuracy > bestAccuracy) {
        bestAccuracy = accuracy;
        strongestCategory = category;
      }
      if (accuracy < worstAccuracy) {
        worstAccuracy = accuracy;
        weakestCategory = category;
      }
    }
    
    const oneWeekAgo = new Date();
    oneWeekAgo.setDate(oneWeekAgo.getDate() - 7);
    const oneMonthAgo = new Date();
    oneMonthAgo.setMonth(oneMonthAgo.getMonth() - 1);
    
    const weeklyProgress = allProgress.filter(p => new Date(p.lastSeen) > oneWeekAgo).length;
    const monthlyProgress = allProgress.filter(p => new Date(p.lastSeen) > oneMonthAgo).length;
    
    return {
      totalWordsLearned: allProgress.filter(p => p.attempts > 0).length,
      averageResponseTime: totalAttempts > 0 ? totalResponseTime / totalAttempts : 0,
      accuracyRate: totalAttempts > 0 ? (totalCorrect / totalAttempts) * 100 : 0,
      learningVelocity: weeklyProgress / 7,
      retentionRate: allProgress.filter(p => p.lastResponseCorrect).length / Math.max(allProgress.length, 1) * 100,
      weeklyProgress,
      monthlyProgress,
      strongestCategory,
      weakestCategory,
      dailyStreak: maxStreak
    };
  }

  async clearAllProgress(): Promise<void> {
    const keys = await storage.getAllKeys();
    const progressKeys = keys.filter(key => key.startsWith('progress_') || key.startsWith('completed_'));
    if (progressKeys.length > 0) {
      await storage.multiRemove(progressKeys);
    }
  }
